import React, { useState } from 'react';
import { GoogleLogin } from 'react-google-login';

// refresh token
import { refreshToken } from './refreshToken';

//this will configure the .env file in the application
require('dotenv').config();

//google cilent Id 
// This is to load your API keys from .env
//const clientId = process.env.Google_clientID ;
const clientId ='808887656812-1tfa2ft0pom3hfttk2ol05ua8naklelk.apps.googleusercontent.com';

function Login() {

  const[name,setName]= useState("");

  const[email,setEmail]= useState("");

  const[url,setUrl]= useState("");

  const responseGoogle = (response) => {
    console.log(response);

    setName(response.profileObj.name);

    setEmail(response.profileObj.email);

    setUrl(response.profileObj.imageUrl);


    alert(
      'Logged in successfully welcome!!!} ðŸ˜. \n See console for full profile object.'
    );
    refreshToken(response);
  };

  const onFailure = (res) => {
    console.log('Login failed: res:', res);
    alert(
      'Failed to login. ðŸ˜¢ '
    );
  };

  return (
    <div>
      <h1>Login with Google</h1>
      <h2>Username: {name} </h2>
      <h2>Email ðŸ“§: {email}</h2>
      <img src={url} alt={name} />

      <GoogleLogin
        clientId={clientId}
        buttonText="Login"
        onSuccess={responseGoogle}
        onFailure={onFailure}
        cookiePolicy={'single_host_origin'}
        isSignedIn={true}
      />
    </div>
  );
}

export default Login;
